import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [leaves, setLeaves] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [leaveForm, setLeaveForm] = useState({
    leaveType: '',
    fromDate: '',
    toDate: '',
    reason: '',
  });
  const [attendanceForm, setAttendanceForm] = useState({
    date: '',
    status: 'Present',
    notes: '',
  });
  const [error, setError] = useState('');
  const { logout, user } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    fetchLeaves();
    fetchAttendance();
  }, []);

  const fetchLeaves = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/leaves/employee', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      if (response.ok) {
        setLeaves(data);
      } else {
        setError(data.message || 'Failed to fetch leaves');
      }
    } catch (error) {
      setError('Error fetching leaves');
    }
  };

  const fetchAttendance = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/attendance/employee', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      if (response.ok) {
        setAttendance(data);
      } else {
        setError(data.message || 'Failed to fetch attendance');
      }
    } catch (error) {
      setError('Error fetching attendance');
    }
  };

  const handleLeaveSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/leaves', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify(leaveForm),
      });
      const data = await response.json();
      if (response.ok) {
        setLeaveForm({ leaveType: '', fromDate: '', toDate: '', reason: '' });
        fetchLeaves();
      } else {
        setError(data.message || 'Failed to apply leave');
      }
    } catch (error) {
      setError('Error applying leave');
    }
  };

  const handleAttendanceSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/attendance', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify(attendanceForm),
      });
      const data = await response.json();
      if (response.ok) {
        setAttendanceForm({ date: '', status: 'Present', notes: '' });
        fetchAttendance();
      } else {
        setError(data.message || 'Failed to record attendance');
      }
    } catch (error) {
      setError('Error recording attendance');
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="employee-dashboard">
      <div className="dashboard-header">
        <h2>Employee Dashboard - {user?.name}</h2>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </div>
      {error && <div className="error-message">{error}</div>}
      <div className="section">
        <h3>Apply for Leave</h3>
        <form onSubmit={handleLeaveSubmit}>
          <div className="form-group">
            <label>Leave Type</label>
            <select
              name="leaveType"
              value={leaveForm.leaveType}
              onChange={(e) => setLeaveForm({ ...leaveForm, leaveType: e.target.value })}
              required
            >
              <option value="">Select Type</option>
              <option value="Casual">Casual</option>
              <option value="Sick">Sick</option>
              <option value="Emergency">Emergency</option>
            </select>
          </div>
          <div className="form-group">
            <label>From Date</label>
            <input
              type="date"
              value={leaveForm.fromDate}
              onChange={(e) => setLeaveForm({ ...leaveForm, fromDate: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>To Date</label>
            <input
              type="date"
              value={leaveForm.toDate}
              onChange={(e) => setLeaveForm({ ...leaveForm, toDate: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Reason</label>
            <input
              type="text"
              value={leaveForm.reason}
              onChange={(e) => setLeaveForm({ ...leaveForm, reason: e.target.value })}
              required
            />
          </div>
          <button type="submit" className="submit-button">Apply Leave</button>
        </form>
        <h3>My Leave Requests</h3>
        {leaves.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Type</th>
                <th>From</th>
                <th>To</th>
                <th>Reason</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {leaves.map((leave) => (
                <tr key={leave._id}>
                  <td>{leave.leaveType}</td>
                  <td>{new Date(leave.fromDate).toLocaleDateString()}</td>
                  <td>{new Date(leave.toDate).toLocaleDateString()}</td>
                  <td>{leave.reason}</td>
                  <td>{leave.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No leave requests found.</p>
        )}
      </div>
      <div className="section">
        <h3>Maintain Attendance</h3>
        <form onSubmit={handleAttendanceSubmit}>
          <div className="form-group">
            <label>Date</label>
            <input
              type="date"
              value={attendanceForm.date}
              onChange={(e) => setAttendanceForm({ ...attendanceForm, date: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Status</label>
            <select
              name="status"
              value={attendanceForm.status}
              onChange={(e) => setAttendanceForm({ ...attendanceForm, status: e.target.value })}
              required
            >
              <option value="Present">Present</option>
              <option value="Absent">Absent</option>
              <option value="Late">Late</option>
            </select>
          </div>
          <div className="form-group">
            <label>Notes</label>
            <input
              type="text"
              value={attendanceForm.notes}
              onChange={(e) => setAttendanceForm({ ...attendanceForm, notes: e.target.value })}
            />
          </div>
          <button type="submit" className="submit-button">Record Attendance</button>
        </form>
        <h3>My Attendance</h3>
        {attendance.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Status</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
              {attendance.map((att) => (
                <tr key={att._id}>
                  <td>{new Date(att.date).toLocaleDateString()}</td>
                  <td>{att.status}</td>
                  <td>{att.notes || '-'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No attendance records found.</p>
        )}
      </div>
    </div>
  );
};

export default EmployeeDashboard;
