import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { applyLeave, getEmployeeLeaves } from '../services/api';
import { AuthContext } from '../context/AuthContext';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [leaveData, setLeaveData] = useState({
    leaveType: 'Casual',
    fromDate: '',
    toDate: '',
    reason: '',
  });
  const [leaves, setLeaves] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    fetchLeaves();
  }, []);

  const fetchLeaves = async () => {
    try {
      const response = await getEmployeeLeaves();
      setLeaves(response.data);
    } catch (error) {
      setError('Failed to fetch leaves');
    }
  };

  const handleChange = (e) => {
    setLeaveData({
      ...leaveData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await applyLeave(leaveData);
      setSuccess('Leave applied successfully!');
      setLeaveData({
        leaveType: 'Casual',
        fromDate: '',
        toDate: '',
        reason: '',
      });
      fetchLeaves();
      setTimeout(() => setSuccess(''), 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to apply leave');
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="employee-dashboard">
      <div className="dashboard-header">
        <h2>Welcome, {user?.name}</h2>
        <button onClick={handleLogout} className="logout-button">
          Logout
        </button>
      </div>

      <div className="dashboard-content">
        <div className="leave-form">
          <h3>Apply for Leave</h3>
          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">{success}</div>}
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Leave Type</label>
              <select
                name="leaveType"
                value={leaveData.leaveType}
                onChange={handleChange}
              >
                <option value="Casual">Casual</option>
                <option value="Sick">Sick</option>
                <option value="Emergency">Emergency</option>
              </select>
            </div>
            <div className="form-group">
              <label>From Date</label>
              <input
                type="date"
                name="fromDate"
                value={leaveData.fromDate}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>To Date</label>
              <input
                type="date"
                name="toDate"
                value={leaveData.toDate}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Reason</label>
              <textarea
                name="reason"
                value={leaveData.reason}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="submit-button">
              Apply Leave
            </button>
          </form>
        </div>

        <div className="leave-history">
          <h3>Your Leave History</h3>
          {leaves.length === 0 ? (
            <p>No leave applications found</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Leave Type</th>
                  <th>From</th>
                  <th>To</th>
                  <th>Reason</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {leaves.map((leave) => (
                  <tr key={leave._id}>
                    <td>{leave.leaveType}</td>
                    <td>{new Date(leave.fromDate).toLocaleDateString()}</td>
                    <td>{new Date(leave.toDate).toLocaleDateString()}</td>
                    <td>{leave.reason}</td>
                    <td className={`status-${leave.status.toLowerCase()}`}>
                      {leave.status}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
