import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../context/AuthContext';
import './Dashboard.css';

const AdminDashboard = () => {
  const { user } = useContext(AuthContext);
  const [leaves, setLeaves] = useState([]);
  const [filters, setFilters] = useState({
    status: '',
    department: '',
    employeeId: '',
    fromDate: '',
    toDate: '',
    sortBy: 'createdAt',
    order: 'desc',
  });
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    const fetchLeaves = async () => {
      try {
        const response = await axios.get('/api/leaves/admin', {
          params: filters,
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setLeaves(response.data);
      } catch (error) {
        console.error('Error fetching leaves:', error);
      }
    };
    
    const fetchDepartments = async () => {
      try {
        const response = await axios.get('/api/departments', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setDepartments(response.data);
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };
    
    fetchLeaves();
    fetchDepartments();
  }, [filters]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleStatusUpdate = async (leaveId, status) => {
    try {
      const comment = prompt('Enter comment (optional):');
      await axios.patch(`/api/leaves/${leaveId}`, { status, comment }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      
      setLeaves(leaves.map(leave => 
        leave._id === leaveId ? { ...leave, status, comment } : leave
      ));
    } catch (error) {
      console.error('Error updating leave:', error);
    }
  };

  return (
    <div className="dashboard-container">
      <h1>Admin Dashboard</h1>
      
      <div className="filters-section">
        <h2>Filters</h2>
        <div className="filter-row">
          <div className="filter-group">
            <label>Status</label>
            <select
              name="status"
              value={filters.status}
              onChange={handleFilterChange}
            >
              <option value="">All</option>
              <option value="Pending">Pending</option>
              <option value="Approved">Approved</option>
              <option value="Rejected">Rejected</option>
            </select>
          </div>
          
          <div className="filter-group">
            <label>Department</label>
            <select
              name="department"
              value={filters.department}
              onChange={handleFilterChange}
            >
              <option value="">All</option>
              {departments.map(dept => (
                <option key={dept} value={dept}>{dept}</option>
              ))}
            </select>
          </div>
          
          <div className="filter-group">
            <label>Employee ID</label>
            <input
              type="text"
              name="employeeId"
              value={filters.employeeId}
              onChange={handleFilterChange}
              placeholder="Search by ID"
            />
          </div>
        </div>
        
        <div className="filter-row">
          <div className="filter-group">
            <label>From Date</label>
            <input
              type="date"
              name="fromDate"
              value={filters.fromDate}
              onChange={handleFilterChange}
            />
          </div>
          
          <div className="filter-group">
            <label>To Date</label>
            <input
              type="date"
              name="toDate"
              value={filters.toDate}
              onChange={handleFilterChange}
            />
          </div>
          
          <div className="filter-group">
            <label>Sort By</label>
            <select
              name="sortBy"
              value={filters.sortBy}
              onChange={handleFilterChange}
            >
              <option value="createdAt">Application Date</option>
              <option value="fromDate">Leave Start Date</option>
            </select>
            <select
              name="order"
              value={filters.order}
              onChange={handleFilterChange}
            >
              <option value="desc">Newest First</option>
              <option value="asc">Oldest First</option>
            </select>
          </div>
        </div>
      </div>
      
      <div className="leave-requests-section">
        <h2>Leave Requests</h2>
        {leaves.length === 0 ? (
          <p>No leave requests found</p>
        ) : (
          <div className="leave-list">
            {leaves.map((leave) => (
              <div key={leave._id} className={`leave-item ${leave.status.toLowerCase()}`}>
                <div className="leave-header">
                  <span className="employee-info">
                    {leave.name} ({leave.employeeId}) - {leave.department}
                  </span>
                  <span className="leave-status">{leave.status}</span>
                </div>
                <div className="leave-details">
                  <div className="leave-type">{leave.leaveType} Leave</div>
                  <div className="leave-dates">
                    {new Date(leave.fromDate).toLocaleDateString()} - {new Date(leave.toDate).toLocaleDateString()}
                  </div>
                  <div className="leave-reason">{leave.reason}</div>
                  {leave.comment && (
                    <div className="leave-comment">
                      <strong>Comment:</strong> {leave.comment}
                    </div>
                  )}
                  <div className="leave-actions">
                    {leave.status === 'Pending' && (
                      <>
                        <button 
                          onClick={() => handleStatusUpdate(leave._id, 'Approved')}
                          className="btn-approve"
                        >
                          Approve
                        </button>
                        <button 
                          onClick={() => handleStatusUpdate(leave._id, 'Rejected')}
                          className="btn-reject"
                        >
                          Reject
                        </button>
                      </>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
