import React from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();

  const handleRoleSelection = (role) => {
    if (role === 'employee') {
      navigate('/login');
    } else if (role === 'admin') {
      navigate('/login');
    }
  };

  const handleRegister = (role) => {
    navigate('/register');
  };

  return (
    <div className="homepage-container">
      <h1>Leave Management System</h1>
      <div className="container-group">
        <div className="container">
          <h2>Employee</h2>
          <button onClick={() => handleRoleSelection('employee')} className="role-btn">
            Login
          </button>
          <button onClick={() => handleRegister('employee')} className="role-btn">
            Register
          </button>
        </div>
        <div className="container">
          <h2>Admin</h2>
          <button onClick={() => handleRoleSelection('admin')} className="role-btn">
            Login
          </button>
          <button onClick={() => handleRegister('admin')} className="role-btn">
            Register
          </button>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
