import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import EmployeeDashboard from './components/EmployeeDashboard';
import AdminDashboard from './components/AdminDashboard';
import Navbar from './components/Navbar';
import HomePage from './components/HomePage';
import AuthContext from './context/AuthContext';

axios.defaults.baseURL = 'http://localhost:5000';
axios.defaults.withCredentials = true;

function App() {
  const [authInfo, setAuthInfo] = useState(() => {
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null;
    console.log('Initial authInfo:', { token, user, isAuthenticated: !!token });
    return { token, user, isAuthenticated: !!token };
  });

  useEffect(() => {
    const verifyToken = async () => {
      if (authInfo.token) {
        try {
          const response = await axios.get('/api/verify-token', {
            headers: { Authorization: `Bearer ${authInfo.token}` },
          });
          console.log('Token verification response:', response.data);
          if (!response.data.valid) {
            logout();
          }
        } catch (error) {
          console.error('Token verification error:', error);
          logout();
        }
      }
    };
    verifyToken();
  }, [authInfo.token]);

  const login = (token, user) => {
    console.log('Login called with:', { token, user });
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(user));
    setAuthInfo({ token, user, isAuthenticated: true });
    console.log('Updated authInfo:', { token, user, isAuthenticated: true });
  };

  const logout = () => {
    console.log('Logging out');
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setAuthInfo({ token: null, user: null, isAuthenticated: false });
  };

  console.log('Current authInfo in render:', authInfo);

  return (
    <AuthContext.Provider value={{ ...authInfo, login, logout }}>
      <Router>
        <div className="App">
          <Navbar />
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route
              path="/login"
              element={!authInfo.isAuthenticated ? <Login /> : <Navigate to="/employee" />}
            />
            <Route
              path="/register"
              element={!authInfo.isAuthenticated ? <Register /> : <Navigate to="/employee" />}
            />
            <Route
              path="/employee"
              element={authInfo.isAuthenticated ? <EmployeeDashboard /> : <Navigate to="/login" />}
            />
            <Route
              path="/admin"
              element={<Navigate to="/employee" />} // Redirect admins to employee page
            />
          </Routes>
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
