import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [leaves, setLeaves] = useState([]);
  const [loadingId, setLoadingId] = useState(null);
  const [filters, setFilters] = useState({
    name: '',
    fromDate: '',
    toDate: '',
    status: '',
  });
  const [sortOrder, setSortOrder] = useState('recent');
  const [comment, setComment] = useState('');
  
  // Assuming you have a way to get the logged-in user's role
  const userRole = 'admin'; // Replace with actual logic to get the logged-in user's role (e.g., from context or auth state)
  const loggedInUserId = '12345'; // Replace with actual logged-in user's ID

  useEffect(() => {
    const fetchLeaves = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/employee-leaves');
        setLeaves(response.data);
      } catch (error) {
        console.error('Error fetching leaves:', error);
      }
    };
    fetchLeaves();
  }, []);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleLeaveStatusChange = async (id, status) => {
    setLoadingId(id);
    try {
      const response = await axios.patch(`http://localhost:5000/api/approve-leave/${id}`, {
        status,
        comment,
      });
      alert(`Leave ${status.toLowerCase()} successfully!`);
      setLeaves((prevLeaves) =>
        prevLeaves.map((leave) =>
          leave._id === id ? { ...leave, status, comment: response.data.leave.comment } : leave
        )
      );
      setComment('');
    } catch (error) {
      console.error('Error updating leave status:', error);
      alert('Failed to update leave status');
    } finally {
      setLoadingId(null);
    }
  };

  const filteredLeaves = leaves
    .filter((leave) => {
      // Only show employee's own leaves if the user is an employee
      if (userRole === 'employee' && leave.userId !== loggedInUserId) {
        return false;
      }

      const matchesName = filters.name
        ? leave.name.toLowerCase().includes(filters.name.toLowerCase())
        : true;
      const matchesStatus = filters.status ? leave.status === filters.status : true;
      const matchesFromDate = filters.fromDate
        ? new Date(leave.fromDate) >= new Date(filters.fromDate)
        : true;
      const matchesToDate = filters.toDate
        ? new Date(leave.toDate) <= new Date(filters.toDate)
        : true;
      return matchesName && matchesStatus && matchesFromDate && matchesToDate;
    })
    .sort((a, b) => {
      const dateA = new Date(a.createdAt);
      const dateB = new Date(b.createdAt);
      return sortOrder === 'recent' ? dateB - dateA : dateA - dateB;
    });

  return (
    <div className="admin-container">
      <div className="admin-card">
        <h1 className="admin-title">Admin Dashboard</h1>

        {/* Filters */}
        {userRole === 'admin' && (
          <div className="filter-section">
            <div className="filter-row">
              <div className="filter-group">
                <input
                  type="text"
                  name="name"
                  placeholder="Employee Name"
                  value={filters.name}
                  onChange={handleFilterChange}
                />
              </div>
              <div className="filter-group">
                <input
                  type="date"
                  name="fromDate"
                  value={filters.fromDate}
                  onChange={handleFilterChange}
                />
              </div>
              <div className="filter-group">
                <input
                  type="date"
                  name="toDate"
                  value={filters.toDate}
                  onChange={handleFilterChange}
                />
              </div>
              <div className="filter-group">
                <select
                  name="status"
                  value={filters.status}
                  onChange={handleFilterChange}
                >
                  <option value="">All Statuses</option>
                  <option value="Pending">Pending</option>
                  <option value="Approved">Approved</option>
                  <option value="Rejected">Rejected</option>
                </select>
              </div>
            </div>
            <div className="sort-group">
              <label>Sort by:</label>
              <select
                value={sortOrder}
                onChange={(e) => setSortOrder(e.target.value)}
              >
                <option value="recent">Most Recent</option>
                <option value="oldest">Oldest</option>
              </select>
            </div>
          </div>
        )}

        {/* Leave Requests Table */}
        <div className="leave-table-container">
          <table className="leave-table">
            <thead>
              <tr>
                <th>Employee Name</th>
                <th>Leave Type</th>
                <th>From Date</th>
                <th>To Date</th>
                <th>Reason</th>
                <th>Status</th>
                {userRole === 'admin' && <th>Actions</th>}
              </tr>
            </thead>
            <tbody>
              {filteredLeaves.length === 0 ? (
                <tr>
                  <td colSpan={userRole === 'admin' ? 7 : 6} className="no-results">
                    No leave requests found matching your criteria
                  </td>
                </tr>
              ) : (
                filteredLeaves.map((leave) => (
                  <tr key={leave._id}>
                    <td>{leave.name}</td>
                    <td>{leave.leaveType}</td>
                    <td>{new Date(leave.fromDate).toLocaleDateString()}</td>
                    <td>{new Date(leave.toDate).toLocaleDateString()}</td>
                    <td>{leave.reason}</td>
                    <td>
                      <span className={`status-badge ${leave.status.toLowerCase()}`}>
                        {leave.status}
                      </span>
                    </td>
                    {userRole === 'admin' && (
                      <td>
                        {leave.status === 'Pending' && (
                          <div className="action-buttons">
                            <textarea
                              placeholder="Add a comment (optional)"
                              value={comment}
                              onChange={(e) => setComment(e.target.value)}
                              className="comment-box"
                            />
                            <div className="button-group">
                              <button
                                onClick={() => handleLeaveStatusChange(leave._id, 'Approved')}
                                disabled={loadingId === leave._id}
                                className="approve-btn"
                              >
                                {loadingId === leave._id ? 'Approving...' : 'Approve'}
                              </button>
                              <button
                                onClick={() => handleLeaveStatusChange(leave._id, 'Rejected')}
                                disabled={loadingId === leave._id}
                                className="reject-btn"
                              >
                                {loadingId === leave._id ? 'Rejecting...' : 'Reject'}
                              </button>
                            </div>
                          </div>
                        )}
                      </td>
                    )}
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
