import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../context/AuthContext';
import './Dashboard.css';

const EmployeeDashboard = () => {
  const { user } = useContext(AuthContext);
  const [leaves, setLeaves] = useState([]);
  const [formData, setFormData] = useState({
    leaveType: 'Casual',
    fromDate: '',
    toDate: '',
    reason: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchLeaves = async () => {
      try {
        const response = await axios.get('/api/leaves/employee', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setLeaves(response.data);
      } catch (error) {
        console.error('Error fetching leaves:', error);
      }
    };
    fetchLeaves();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await axios.post('/api/leaves', formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Leave applied successfully!');
      setLeaves([response.data.leave, ...leaves]);
      setFormData({
        leaveType: 'Casual',
        fromDate: '',
        toDate: '',
        reason: '',
      });
    } catch (error) {
      setError(error.response?.data?.message || 'Error applying leave');
    }
  };

  return (
    <div className="dashboard-container">
      <h1>Welcome, {user?.name}</h1>
      
      <div className="leave-form-section">
        <h2>Apply for Leave</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}
        
        <form onSubmit={handleSubmit} className="leave-form">
          <div className="form-group">
            <label>Leave Type</label>
            <select
              name="leaveType"
              value={formData.leaveType}
              onChange={handleInputChange}
              required
            >
              <option value="Casual">Casual</option>
              <option value="Sick">Sick</option>
              <option value="Emergency">Emergency</option>
            </select>
          </div>
          
          <div className="form-row">
            <div className="form-group">
              <label>From Date</label>
              <input
                type="date"
                name="fromDate"
                value={formData.fromDate}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>To Date</label>
              <input
                type="date"
                name="toDate"
                value={formData.toDate}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>
          
          <div className="form-group">
            <label>Reason</label>
            <textarea
              name="reason"
              value={formData.reason}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <button type="submit" className="btn-primary">
            Apply Leave
          </button>
        </form>
      </div>
      
      <div className="leave-status-section">
        <h2>Your Leave Applications</h2>
        {leaves.length === 0 ? (
          <p>No leave applications found</p>
        ) : (
          <div className="leave-list">
            {leaves.map((leave) => (
              <div key={leave._id} className={`leave-item ${leave.status.toLowerCase()}`}>
                <div className="leave-header">
                  <span className="leave-type">{leave.leaveType} Leave</span>
                  <span className="leave-status">{leave.status}</span>
                </div>
                <div className="leave-dates">
                  {new Date(leave.fromDate).toLocaleDateString()} - {new Date(leave.toDate).toLocaleDateString()}
                </div>
                <div className="leave-reason">{leave.reason}</div>
                {leave.comment && (
                  <div className="leave-comment">
                    <strong>Admin Comment:</strong> {leave.comment}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default EmployeeDashboard;
