import axios from 'axios';

const API = "http://localhost:5000/api/leaves";

// Apply for leave
export const applyLeave = (data) => axios.post(API, data);

// Get all leave requests (Admin view)
export const getAllLeaves = () => axios.get(API);

// Get leaves by employee ID (Employee view)
export const getUserLeaves = (employeeId) => axios.get(`${API}/user/${employeeId}`);

// Update leave status (Admin approval/rejection)
export const updateLeaveStatus = (id, statusData) => axios.put(`${API}/${id}`, statusData);
