import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [leaves, setLeaves] = useState([]);
  const [filters, setFilters] = useState({ name: '', fromDate: '', toDate: '', status: '' });
  const [comment, setComment] = useState('');

  useEffect(() => {
    fetchLeaves();
  }, []);

  const fetchLeaves = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/leaves', { params: filters });
      setLeaves(response.data);
    } catch (error) {
      console.error('Error fetching leaves:', error);
    }
  };

  const handleFilterChange = (e) => setFilters({ ...filters, [e.target.name]: e.target.value });
  const handleStatusUpdate = async (id, status) => {
    await axios.patch(`http://localhost:5000/api/update-status/${id}`, { status, comment });
    fetchLeaves();
  };

  return (
    <div className="admin-container">
      <h1>Admin Dashboard</h1>
      <div className="filter-section">
        <input name="name" placeholder="Name" value={filters.name} onChange={handleFilterChange} />
        <input type="date" name="fromDate" value={filters.fromDate} onChange={handleFilterChange} />
        <input type="date" name="toDate" value={filters.toDate} onChange={handleFilterChange} />
        <select name="status" value={filters.status} onChange={handleFilterChange}>
          <option value="">All</option>
          <option value="Pending">Pending</option>
          <option value="Approved">Approved</option>
          <option value="Rejected">Rejected</option>
        </select>
        <button onClick={fetchLeaves}>Filter</button>
      </div>
      <div className="leave-list">
        {leaves.map((leave) => (
          <div key={leave._id} className="leave-item">
            <span>{leave.name} - {new Date(leave.fromDate).toLocaleDateString()} to {new Date(leave.toDate).toLocaleDateString()}</span>
            <select value={leave.status} onChange={(e) => handleStatusUpdate(leave._id, e.target.value)}>
              <option value="Pending">Pending</option>
              <option value="Approved">Approved</option>
              <option value="Rejected">Rejected</option>
            </select>
            <input type="text" placeholder="Comment" value={comment} onChange={(e) => setComment(e.target.value)} />
            <button onClick={() => handleStatusUpdate(leave._id, leave.status)}>Save</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
