import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import './Auth.css';

const Login = () => {
  const [credentials, setCredentials] = useState({ employeeId: '', password: '', role: 'employee' });
  const [error, setError] = useState('');
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!credentials.employeeId || !credentials.password) {
      setError('All fields are required');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(credentials),
      });
      const data = await response.json();

      if (response.ok) {
        login(data.token, data.user);
        navigate(data.user.role === 'admin' ? '/admin' : '/employee');
      } else {
        setError(data.message || 'Login failed');
      }
    } catch (error) {
      setError('Error connecting to server');
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-split">
        {/* Admin Login */}
        <div className="auth-card admin-card">
          <h2>Admin Login</h2>
          {error && <div className="error-message">{error}</div>}
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Employee ID</label>
              <input
                type="text"
                name="employeeId"
                value={credentials.employeeId}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={credentials.password}
                onChange={handleChange}
                required
              />
            </div>
            <input type="hidden" name="role" value="admin" />
            <button type="submit" className="auth-button">Login as Admin</button>
          </form>
        </div>

        {/* Employee Login */}
        <div className="auth-card employee-card">
          <h2>Employee Login</h2>
          {error && <div className="error-message">{error}</div>}
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Employee ID</label>
              <input
                type="text"
                name="employeeId"
                value={credentials.employeeId}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={credentials.password}
                onChange={handleChange}
                required
              />
            </div>
            <input type="hidden" name="role" value="employee" />
            <button type="submit" className="auth-button">Login as Employee</button>
          </form>
          <p className="auth-link">
            Don't have an account? <span onClick={() => navigate('/register')}>Register</span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
