import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [leaves, setLeaves] = useState([]);
  const [loadingId, setLoadingId] = useState(null);
  const [filters, setFilters] = useState({
    name: '',
    fromDate: '',
    toDate: '',
    status: '',
  });
  const [sortOrder, setSortOrder] = useState('recent');
  const [comment, setComment] = useState('');

  useEffect(() => {
    const fetchLeaves = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/employee-leaves');
        setLeaves(response.data);
      } catch (error) {
        console.error('Error fetching leaves:', error);
      }
    };
    fetchLeaves();
  }, []);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleLeaveStatusChange = async (id, status) => {
    setLoadingId(id);
    try {
      const response = await axios.patch(`http://localhost:5000/api/approve-leave/${id}`, {
        status,
        comment,
      });
      alert(`Leave ${status.toLowerCase()} successfully!`);
      setLeaves((prevLeaves) =>
        prevLeaves.map((leave) =>
          leave._id === id ? { ...leave, status, comment: response.data.leave.comment } : leave
        )
      );
      setComment('');
    } catch (error) {
      console.error('Error updating leave status:', error);
      alert('Failed to update leave status');
    } finally {
      setLoadingId(null);
    }
  };

  const filteredLeaves = leaves
    .filter((leave) => {
      const matchesName = filters.name
        ? leave.name.toLowerCase().includes(filters.name.toLowerCase())
        : true;
      const matchesStatus = filters.status ? leave.status === filters.status : true;
      const matchesFromDate = filters.fromDate
        ? new Date(leave.fromDate) >= new Date(filters.fromDate)
        : true;
      const matchesToDate = filters.toDate
        ? new Date(leave.toDate) <= new Date(filters.toDate)
        : true;
      return matchesName && matchesStatus && matchesFromDate && matchesToDate;
    })
    .sort((a, b) => {
      const dateA = new Date(a.createdAt);
      const dateB = new Date(b.createdAt);
      return sortOrder === 'recent' ? dateB - dateA : dateA - dateB;
    });

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-6xl mx-auto bg-white p-8 rounded-lg shadow-lg">
        <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

        {/* Filters */}
        <div className="mb-6 grid grid-cols-1 md:grid-cols-4 gap-4">
          <input
            type="text"
            name="name"
            placeholder="Filter by Employee Name"
            value={filters.name}
            onChange={handleFilterChange}
            className="border p-2 rounded"
          />
          <input
            type="date"
            name="fromDate"
            value={filters.fromDate}
            onChange={handleFilterChange}
            className="border p-2 rounded"
          />
          <input
            type="date"
            name="toDate"
            value={filters.toDate}
            onChange={handleFilterChange}
            className="border p-2 rounded"
          />
          <select
            name="status"
            value={filters.status}
            onChange={handleFilterChange}
            className="border p-2 rounded"
          >
            <option value="">All Statuses</option>
            <option value="Pending">Pending</option>
            <option value="Approved">Approved</option>
            <option value="Rejected">Rejected</option>
          </select>
        </div>

        {/* Sorting */}
        <div className="mb-4">
          <label className="mr-2">Sort by:</label>
          <select
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
            className="border p-2 rounded"
          >
            <option value="recent">Most Recent</option>
            <option value="oldest">Oldest</option>
          </select>
        </div>

        {/* Leave Requests Table */}
        <table className="w-full border-collapse">
          <thead>
            <tr className="bg-gray-200">
              <th className="border p-2">Employee Name</th>
              <th className="border p-2">Leave Type</th>
              <th className="border p-2">From Date</th>
              <th className="border p-2">To Date</th>
              <th className="border p-2">Reason</th>
              <th className="border p-2">Status</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredLeaves.map((leave) => (
              <tr key={leave._id} className="hover:bg-gray-50">
                <td className="border p-2">{leave.name}</td>
                <td className="border p-2">{leave.leaveType}</td>
                <td className="border p-2">{new Date(leave.fromDate).toLocaleDateString()}</td>
                <td className="border p-2">{new Date(leave.toDate).toLocaleDateString()}</td>
                <td className="border p-2">{leave.reason}</td>
                <td className="border p-2">
                  <span
                    className={`${
                      leave.status === 'Approved'
                        ? 'text-green-500'
                        : leave.status === 'Rejected'
                        ? 'text-red-500'
                        : 'text-yellow-500'
                    }`}
                  >
                    {leave.status}
                  </span>
                </td>
                <td className="border p-2">
                  {leave.status === 'Pending' && (
                    <div className="flex flex-col gap-2">
                      <textarea
                        placeholder="Add a comment (optional)"
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        className="border p-2 rounded w-full"
                      />
                      <div className="flex gap-2">
                        <button
                          onClick={() => handleLeaveStatusChange(leave._id, 'Approved')}
                          disabled={loadingId === leave._id}
                          className="bg-green-500 text-white py-1 px-3 rounded hover:bg-green-600 disabled:opacity-50"
                        >
                          {loadingId === leave._id ? 'Approving...' : 'Approve'}
                        </button>
                        <button
                          onClick={() => handleLeaveStatusChange(leave._id, 'Rejected')}
                          disabled={loadingId === leave._id}
                          className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600 disabled:opacity-50"
                        >
                          {loadingId === leave._id ? 'Rejecting...' : 'Reject'}
                        </button>
                      </div>
                    </div>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
