import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [leaveData, setLeaveData] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    employeeId: '',
    fromDate: '',
    toDate: '',
    reason: '',
    leaveType: 'Casual',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Fetch leave data from the API
  useEffect(() => {
    const fetchLeaveData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/employee-leaves');
        setLeaveData(response.data);
      } catch (error) {
        console.error('Error fetching leave data:', error);
        setError('Failed to load leave data.');
      }
    };
    fetchLeaveData();
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validate the form before submitting
  const validateForm = () => {
    if (!formData.name || !formData.employeeId || !formData.fromDate || !formData.toDate || !formData.reason) {
      return 'All fields are required';
    }
    const fromDate = new Date(formData.fromDate);
    const toDate = new Date(formData.toDate);
    if (fromDate > toDate) {
      return 'From Date cannot be later than To Date';
    }
    if (fromDate < new Date()) {
      return 'From Date cannot be in the past';
    }
    return '';
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/apply-leave', formData);
      setSuccess('Leave applied successfully!');
      setLeaveData((prevData) => [...prevData, response.data.leave]);  // Update leave data
      setFormData({
        name: '',
        employeeId: '',
        fromDate: '',
        toDate: '',
        reason: '',
        leaveType: 'Casual',
      });
    } catch (error) {
      setError(error.response?.data?.message || 'Error applying leave');
    }
  };

  return (
    <div className="employee-container">
      <div className="employee-card">
        <h1 className="employee-title">Employee Dashboard</h1>

        {/* Leave Application Form */}
        <div className="form-section">
          <h2 className="section-title">Apply for Leave</h2>
          {error && <p className="error-message">{error}</p>}
          {success && <p className="success-message">{success}</p>}
          <form onSubmit={handleSubmit} className="leave-form">
            <div className="form-group">
              <input
                type="text"
                name="name"
                placeholder="Employee Name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                name="employeeId"
                placeholder="Employee ID"
                value={formData.employeeId}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-row">
              <div className="form-group">
                <label>From Date</label>
                <input
                  type="date"
                  name="fromDate"
                  value={formData.fromDate}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>To Date</label>
                <input
                  type="date"
                  name="toDate"
                  value={formData.toDate}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            <div className="form-group">
              <textarea
                name="reason"
                placeholder="Leave Reason"
                value={formData.reason}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Leave Type</label>
              <select
                name="leaveType"
                value={formData.leaveType}
                onChange={handleInputChange}
              >
                <option value="Casual">Casual</option>
                <option value="Sick">Sick</option>
                <option value="Emergency">Emergency</option>
              </select>
            </div>
            <button type="submit" className="submit-btn">
              Apply for Leave
            </button>
          </form>
        </div>

        {/* Leave Status */}
        <div className="status-section">
          <h2 className="section-title">Leave Status</h2>
          {leaveData.length === 0 ? (
            <p className="no-leaves">No leave applications found</p>
          ) : (
            <div className="leave-list">
              {leaveData.map((leave, index) => (
                <div key={index} className="leave-item">
                  <div className="leave-header">
                    <span className="leave-name">{leave.name}</span>
                    <span className={`status-badge ${leave.status.toLowerCase()}`}>
                      {leave.status}
                    </span>
                  </div>
                  <div className="leave-dates">
                    {new Date(leave.fromDate).toLocaleDateString()} - {new Date(leave.toDate).toLocaleDateString()}
                  </div>
                  <div className="leave-reason">{leave.reason}</div>
                  {leave.comment && (
                    <div className="leave-comment">
                      <strong>Admin Comment:</strong> {leave.comment}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
