import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext'; // Updated import
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [leaves, setLeaves] = useState([]);
  const [filters, setFilters] = useState({
    status: '',
    department: '',
    employeeId: '',
    fromDate: '',
    toDate: '',
    sortBy: 'createdAt',
    order: 'desc',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    fetchLeaves();
  }, []);

  const fetchLeaves = async () => {
    setLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const query = new URLSearchParams(filters).toString();
      const response = await fetch(`http://localhost:5000/api/leaves/admin?${query}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      if (response.ok) {
        setLeaves(data);
      } else {
        setError(data.message || 'Failed to fetch leaves');
      }
    } catch (error) {
      setError('Error connecting to server');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value,
    });
  };

  const handleStatusUpdate = async (id, status, comment = '') => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/leaves/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status, comment }), // Fixed typo: body instead of atualização
      });
      const data = await response.json();
      if (response.ok) {
        fetchLeaves();
      } else {
        setError(data.message || 'Failed to update leave status');
      }
    } catch (error) {
      setError('Error connecting to server');
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </div>

      <div className="filters">
        <h3>Filters</h3>
        <div className="filter-group">
          <select name="status" value={filters.status} onChange={handleFilterChange}>
            <option value="">All Statuses</option>
            <option value="Pending">Pending</option>
            <option value="Approved">Approved</option>
            <option value="Rejected">Rejected</option>
          </select>
          <input
            type="text"
            name="employeeId"
            placeholder="Employee ID"
            value={filters.employeeId}
            onChange={handleFilterChange}
          />
          <input
            type="text"
            name="department"
            placeholder="Department"
            value={filters.department}
            onChange={handleFilterChange}
          />
          <input
            type="date"
            name="fromDate"
            value={filters.fromDate}
            onChange={handleFilterChange}
          />
          <input
            type="date"
            name="toDate"
            value={filters.toDate}
            onChange={handleFilterChange}
          />
          <select name="sortBy" value={filters.sortBy} onChange={handleFilterChange}>
            <option value="createdAt">Created Date</option>
            <option value="fromDate">From Date</option>
          </select>
          <select name="order" value={filters.order} onChange={handleFilterChange}>
            <option value="desc">Newest First</option>
            <option value="asc">Oldest First</option>
          </select>
          <button onClick={fetchLeaves} className="filter-button">Apply Filters</button>
        </div>
      </div>

      <div className="leave-requests">
        <h3>Leave Requests</h3>
        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p className="error-message">{error}</p>
        ) : leaves.length === 0 ? (
          <p>No leave requests found</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Employee</th>
                <th>ID</th>
                <th>Department</th>
                <th>Leave Type</th>
                <th>From</th>
                <th>To</th>
                <th>Reason</th>
                <th>Status</th>
                <th>Comment</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {leaves.map((leave) => (
                <tr key={leave._id}>
                  <td>{leave.name}</td>
                  <td>{leave.employeeId}</td>
                  <td>{leave.department}</td>
                  <td>{leave.leaveType}</td>
                  <td>{new Date(leave.fromDate).toLocaleDateString()}</td>
                  <td>{new Date(leave.toDate).toLocaleDateString()}</td>
                  <td>{leave.reason}</td>
                  <td className={`status-${leave.status.toLowerCase()}`}>{leave.status}</td>
                  <td>{leave.comment || '-'}</td>
                  <td>
                    {leave.status === 'Pending' && (
                      <>
                        <button
                          onClick={() => {
                            const comment = prompt('Optional comment:');
                            handleStatusUpdate(leave._id, 'Approved', comment);
                          }}
                          className="approve-button"
                        >
                          Approve
                        </button>
                        <button
                          onClick={() => {
                            const comment = prompt('Optional comment:');
                            handleStatusUpdate(leave._id, 'Rejected', comment);
                          }}
                          className="reject-button"
                        >
                          Reject
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
