import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import EmployeeDashboard from './components/EmployeeDashboard';
import AdminDashboard from './components/AdminDashboard';
import Navbar from './components/Navbar';
import HomePage from './components/HomePage'; // Add HomePage
import AuthContext from './context/AuthContext';

axios.defaults.baseURL = 'http://localhost:5000';
axios.defaults.withCredentials = true; // Ensure credentials are sent with requests

function App() {
  const [authInfo, setAuthInfo] = useState(() => {
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null;
    return { token, user, isAuthenticated: !!token };
  });

  useEffect(() => {
    // Verify token on mount
    const verifyToken = async () => {
      if (authInfo.token) {
        try {
          const response = await axios.get('/api/verify-token', {
            headers: { Authorization: `Bearer ${authInfo.token}` },
          });
          if (!response.data.valid) {
            logout();
          }
        } catch (error) {
          logout();
        }
      }
    };
    verifyToken();
  }, [authInfo.token]);

  const login = (token, user) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(user));
    setAuthInfo({ token, user, isAuthenticated: true });
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setAuthInfo({ token: null, user: null, isAuthenticated: false });
  };

  return (
    <AuthContext.Provider value={{ ...authInfo, login, logout }}>
      <Router>
        <div className="App">
          <Navbar />
          <Routes>
            <Route path="/" element={<HomePage />} /> {/* Default to HomePage */}
            <Route path="/login" element={!authInfo.isAuthenticated ? <Login /> : <Navigate to={authInfo.user?.role === 'admin' ? '/admin' : '/employee'} />} />
            <Route path="/register" element={!authInfo.isAuthenticated ? <Register /> : <Navigate to={authInfo.user?.role === 'admin' ? '/admin' : '/employee'} />} />
            <Route path="/employee" element={authInfo.isAuthenticated && authInfo.user?.role === 'employee' ? <EmployeeDashboard /> : <Navigate to="/login" />} />
            <Route path="/admin" element={authInfo.isAuthenticated && authInfo.user?.role === 'admin' ? <AdminDashboard /> : <Navigate to="/login" />} />
          </Routes>
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
