import React, { useState } from 'react';

const LeaveForm = () => {
  const [leaveData, setLeaveData] = useState({
    employeeName: '',
    leaveType: '',
    leaveStartDate: '',
    leaveEndDate: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLeaveData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!leaveData.employeeName || !leaveData.leaveType || !leaveData.leaveStartDate || !leaveData.leaveEndDate) {
      alert('All fields are required');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/submitLeave', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(leaveData),
      });

      const result = await response.json();
      if (result.success) {
        alert('Leave submitted successfully!');
        setLeaveData({ employeeName: '', leaveType: '', leaveStartDate: '', leaveEndDate: '' }); // Reset form
      } else {
        alert('Error submitting leave');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('There was an error submitting the leave request');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Employee Name:</label>
        <input
          type="text"
          name="employeeName"
          value={leaveData.employeeName}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Leave Type:</label>
        <input
          type="text"
          name="leaveType"
          value={leaveData.leaveType}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Start Date:</label>
        <input
          type="date"
          name="leaveStartDate"
          value={leaveData.leaveStartDate}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>End Date:</label>
        <input
          type="date"
          name="leaveEndDate"
          value={leaveData.leaveEndDate}
          onChange={handleChange}
        />
      </div>
      <button type="submit">Submit Leave</button>
    </form>
  );
};

export default LeaveForm;
